#		手机型号?
CREATE TABLE ph_product (
#not null  default "" 不能为空 默认为空 不能将null放入 你放null

# 我就转空字符串 所有的必须都加这个 切记 not null default ""
	pid		INT  PRIMARY KEY AUTO_INCREMENT, #商品编号
	family_id INT NOT NULL DEFAULT 0,    #所属型号家族编号
	title VARCHAR(128) NOT NULL DEFAULT '',									#商品主标题
	subtitle VARCHAR(128) NOT NULL DEFAULT '',							#商品详情
	price	DECIMAL(10,2) NOT NULL DEFAULT 9999,								#价格
	promise VARCHAR(64) NOT NULL DEFAULT '',								#承诺
	spec    VARCHAR(64)NOT NULL DEFAULT '',								#规格
	lname		VARCHAR(32) NOT NULL DEFAULT '',								#名称
	os      VARCHAR(32) DEFAULT "安卓（Android）" ,								#系统
	resolution VARCHAR(32) NOT NULL DEFAULT '1920*1080(FHD)',							#分辨率
	battery  VARCHAR(32) NOT NULL DEFAULT '3000mAh-3999mAh',								#电池容量
	postposition  VARCHAR(32) NOT NULL DEFAULT '1200万-1999万',					#后置摄像头
	preposition  VARCHAR(32) NOT NULL DEFAULT '1600万及以上',						#前置摄像头
	cpu  VARCHAR(32) NOT NULL DEFAULT '八核',										#cpu核数
	pre  VARCHAR(32)  NOT NULL DEFAULT '以官网信息为准',		                #频率
	ram  VARCHAR(32)  NOT NULL DEFAULT '6G',										#ram 
	rom  VARCHAR(32)  NOT NULL DEFAULT '64G',										#rom
	is_onsale  BOOLEAN  NOT NULL DEFAULT false,									#是否促销
	detail	VARCHAR(1024) NOT NULL DEFAULT '',							#详细介绍
	sold_time BIGINT NOT NULL DEFAULT 0,  #上架时间 UNIX_TIMESTAMP()PHP中这样使用
	weight varchar(23) not null default "200.00g" ,#重量
	color varchar(23) not null default "白色",
	expire    enum('0','1') NOT NULL DEFAULT '0'
);
